import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation, useHistory } from 'react-router-dom';
import './result.css';

function PlaceList({ x, y }) {
  const [places, setPlaces] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:8000/place/?x=${x}&y=${y}`)
      .then(response => setPlaces(response.data));
  }, [x, y]);

  return (
    <ul className="list">
      {places.map((place, index) => (
        <li key={index} className="place">
          <p>{place.name}</p>
          Rating: {place.rating}<br/>
          Review Count: {place.review_count}<br/>
          Type: {place.type}
        </li>
      ))}
    </ul>
  );
}

function RestaurantList({ x, y }) {
  const [names, setNames] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:8000/restaurant/?x=${x}&y=${y}`)
      .then(response => setNames(response.data.names));
  }, [x, y]);

  return (
    <ul className="list">
      {names.map((name, index) => (
        <li key={index}>
          <p>뤼튼</p>
        </li>
      ))}
    </ul>
  );
}

function Result() {
  const [type, setType] = useState('place'); // 'place' 또는 'restaurant'
  const location = useLocation();
  const history = useHistory();
  const params = new URLSearchParams(location.search);
  const x = params.get('x');
  const y = params.get('y');

  useEffect(() => {
    history.push(`/result/?x=${x}&y=${y}`);
  }, [type, x, y, history]);

  const handleClick = (newType) => {
    setType(newType);
  };

  return (
    <div className="list_wrap">
      <div className="selecting">
        <div className="button-container">
          <ul>
            <li>
              <button className="button" onClick={() => handleClick('place')}>관광지</button>
            </li>
            <li>
              <button className="button" onClick={() => handleClick('restaurant')}>음식점</button>  
            </li>
          </ul>
        </div>
        {type === 'place' ? <PlaceList x={x} y={y} /> : <RestaurantList x={x} y={y} />}
      </div>
    </div>
  );
}

export default Result;
