import { Link } from "react-router-dom";
import { useState } from "react";
import './type.css';

function Type(){
  const [selectedTypes, setSelectedTypes] = useState([]);

  const handleTypeClick = (type) => {
    setSelectedTypes(prevSelectedTypes => {
      if (prevSelectedTypes.includes(type)) {
        return prevSelectedTypes.filter(item => item !== type);
      } else {
        return [...prevSelectedTypes, type];
      }
    });
    console.log(selectedTypes);
  }

  const tourismTypes = ['자연', '역사', '문화', '쇼핑', '레포츠'];

  return (
    <div>
      <div className="App">
        <h1>선호하는 관광타입을 선택해 주세요!</h1>
        {tourismTypes.map(type => (
          <button
            key={type}
            className={selectedTypes.includes(type) ? 'selected' : ''}
            onClick={() => handleTypeClick(type)}
          >
            {type}
          </button>
        ))}
      </div>
      <div className="button-container">
        <Link to={{pathname: "/hashtag", state: { selectedTypes: selectedTypes }}}><button>다음</button></Link>
      </div>
    </div>
  );
}

export default Type
