import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation, useParams } from 'react-router-dom';
import { Switch, Route } from 'react-router';
import './result.css';

function PlaceList({ x, y }) {
  const [places, setPlaces] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:8000/place/?x=${x}&y=${y}`)
      .then(response => setPlaces(response.data));
  }, [x, y]);

  return (
    <ul className="list">
      {places.map((place, index) => (
        <li key={index} className="place">
          <p>{place.name}</p>
          Rating: {place.rating}<br/>
          Review Count: {place.review_count}<br/>
          Type: {place.type}
        </li>
      ))}
    </ul>
  );
}

function RestaurantList({ x, y }) {
  const [names, setNames] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:8000/restaurant/?x=${x}&y=${y}`)
      .then(response => setNames(response.data.names));
  }, [x, y]);

  return (
    <ul className="list">
      {names.map((name, index) => (
        <li key={index}>
          <p>{name}</p>
        </li>
      ))}
    </ul>
  );
}

function Result() {
  const { type } = useParams();
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const x = params.get('x');
  const y = params.get('y');

  return (
    <div className="list_wrap">
      <div className="selecting">
        <div className="button-container">
          <ul>
            <li>
              <Link className="button" to={`/result/place?x=${x}&y=${y}`}>관광지</Link>
            </li>
            <li>
              <Link className="button" to={`/result/restaurant?x=${x}&y=${y}`}>음식점</Link>  
            </li>
          </ul>
        </div>
        <Switch>
          <Route path="/result/place">
            <PlaceList x={x} y={y} />
          </Route>
          <Route path="/result/restaurant">
            <RestaurantList x={x} y={y} />
          </Route>
        </Switch>
      </div>
    </div>
  );
}

export default Result;
