import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { useLocation, Link } from 'react-router-dom';
import PlannerContext from './PlannerContext';
import { Planner } from './PlannerContext';
import './restaurant.css';


function Restaurant() {
  const [names, setNames] = useState([]);
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const { planner, setPlanner } = useContext(PlannerContext);
  const x = params.get('x');
  const y = params.get('y');

  useEffect(() => {
    axios.get(`http://localhost:8000/restaurant/?x=${x}&y=${y}`)
      .then(response => setNames(response.data.names));
  }, [x, y]);

  const addToPlanner = (name) => {
    setPlanner([...planner, name]);
  };

  return (
    <Planner>
        <div className="list_wrap">
            <div className="selecting">
                <div className="button-container">
                    <ul>
                        <li>
                            <Link className="button" to={`/result/?x=${x}&y=${y}`}>관광지</Link>
                        </li>
                        <li>
                            <Link className="button" to={`/restaurant/?x=${x}&y=${y}`}>음식점</Link>  
                        </li>
                    </ul>
                </div>
                {names.length > 0 ? (
                    <ul className="list">
                        {names.map((name, index) => (
                        <li key={index} onClick={() => addToPlanner(name)}>
                            <p>{name}</p>
                        </li>
                        ))}
                    </ul>
                ) : (
                    <div className="loader"></div>
                )}
                <div className="planner">
                    <h2>플래너:</h2>
                    <ul>
                        {planner.map((item, index) => (
                            <li key={index}>{item}</li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    </Planner>
  );
}

export default Restaurant;
