import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation, Link } from 'react-router-dom';

function Result() {
  const [data, setData] = useState([]);
  const [view, setView] = useState('place');

  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const x = params.get('x');
  const y = params.get('y');

  const fetchData = (type) => {
    axios.get(`http://localhost:8000/${type}/?x=${x}&y=${y}`)
      .then(response => {
        if (type === 'place') {
          setData(response.data);
        } else {
          setData(response.data.names);
        }
      });
  };

  useEffect(() => {
    fetchData(view);
  }, [view]);

  return (
    <div className="list_wrap">
      <div className="selecting">
        <div className="button-container">
          <ul>
            <li>
              <button 
                className="button" 
                onClick={() => setView('place')}
              >
                관광지
              </button>
            </li>
            <li>
              <button 
                className="button" 
                onClick={() => setView('restaurant')}
              >
                음식점
              </button>  
            </li>
          </ul>
        </div>
        {view === 'place' ? (
          <ul className="list">
          {data.map((place, index) => (
            <li key={index} className="place">
              <p>{place.name}</p>
              Rating: {place.rating}<br/>
              Review Count: {place.review_count}<br/>
              Type: {place.type}
            </li>
          ))}
        </ul>
        ) : (
          <ul className="list">
          {data.map((name, index) => (
            <li key={index}>
              <p>{name.names}</p>
            </li>
          ))}
        </ul>
        )}
      </div>
    </div>
  );
}

export default Result;
